import React from "react";
import { formatNumber } from "../utils/formatters";

/**
 * DataPreviewTable
 *
 * Props:
 * - theme: color palette object
 * - combined: array of rows like
 *   {
 *     date: string,
 *     newCases: number | null,
 *     confirmed: number | null,
 *     deaths: number | null,
 *     gdpGrowth: number | null
 *   }
 * - title?: string
 */
function DataPreviewTable({ theme, combined, title }) {
    if (!combined || combined.length === 0) {
        return null;
    }

    // Show a small sample: most recent 12 rows
    const sampleSize = 12;
    const rows = combined.slice(-sampleSize).reverse(); // latest first

    return (
        <section className="section">
            {title && <h2 className="section-title">{title}</h2>}
            <p className="section-subtitle">
                Joined dataset used for the chart above. This combines COVID-19
                daily stats and GDP growth for the selected country on a shared
                date axis.
            </p>

            <div className="data-table-wrapper">
                <table className="data-table">
                    <thead>
                        <tr>
                            <th
                                style={{
                                    textAlign: "left",
                                    background: theme.tableHeaderBg,
                                }}
                            >
                                Date
                            </th>
                            <th style={{ background: theme.tableHeaderBg }}>
                                New cases
                            </th>
                            <th style={{ background: theme.tableHeaderBg }}>
                                Total cases
                            </th>
                            <th style={{ background: theme.tableHeaderBg }}>
                                Total deaths
                            </th>
                            <th style={{ background: theme.tableHeaderBg }}>
                                GDP growth (%)
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {rows.map((row) => (
                            <tr key={row.date}>
                                <td
                                    style={{
                                        textAlign: "left",
                                        color: theme.textPrimary,
                                    }}
                                >
                                    {row.date}
                                </td>
                                <td>
                                    {row.newCases != null
                                        ? formatNumber(row.newCases)
                                        : "—"}
                                </td>
                                <td>
                                    {row.confirmed != null
                                        ? formatNumber(row.confirmed)
                                        : "—"}
                                </td>
                                <td>
                                    {row.deaths != null
                                        ? formatNumber(row.deaths)
                                        : "—"}
                                </td>
                                <td>
                                    {row.gdpGrowth != null
                                        ? row.gdpGrowth.toFixed(2)
                                        : "—"}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </section>
    );
}

export default DataPreviewTable;
