// src/pages/Dashboard.js
import React, { useState, useMemo } from "react";
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    Tooltip,
    CartesianGrid,
    ResponsiveContainer,
    Legend,
} from "recharts";
import { useCovidGdpApiData } from "../hooks/useCovidGdpApiData";

const COUNTRY_CONFIG = [
    { key: "US", label: "United States" },
    { key: "DE", label: "Germany" },
    { key: "IT", label: "Italy" },
    { key: "JP", label: "Japan" },
    { key: "CA", label: "Canada" },
    { key: "FR", label: "France" },
    { key: "SE", label: "Sweden" },
    { key: "MX", label: "Mexico" },
    { key: "NZ", label: "New Zealand" },
    { key: "TH", label: "Thailand" },
];

function formatNumber(n) {
    if (n == null) return "—";
    if (Math.abs(n) >= 1_000_000) return (n / 1_000_000).toFixed(1) + "M";
    if (Math.abs(n) >= 1_000) return (n / 1_000).toFixed(1) + "K";
    return n.toLocaleString();
}

function Dashboard() {
    const [selectedCountry, setSelectedCountry] = useState("US");
    const [covidSource, setCovidSource] = useState("who"); // "who" | "te"

    const { loading, error, combined, covidDaily } = useCovidGdpApiData(
        selectedCountry,
        covidSource
    );

    const latest = useMemo(() => {
        if (!covidDaily || covidDaily.length === 0) return null;
        return covidDaily[covidDaily.length - 1];
    }, [covidDaily]);

    const latestGdp = useMemo(() => {
        if (!combined || combined.length === 0) return null;
        const lastWithGdp = [...combined]
            .reverse()
            .find((d) => d.gdpGrowth != null);
        return lastWithGdp || null;
    }, [combined]);

    const covidSourceLabel =
        covidSource === "who"
            ? "WHO / Pomber timeseries"
            : "TradingEconomics (fallback to WHO)";

    return (
        <div style={{ padding: "24px", maxWidth: 1200, margin: "0 auto" }}>
            {/* Header */}
            <header
                style={{
                    marginBottom: 24,
                    display: "flex",
                    flexWrap: "wrap",
                    alignItems: "center",
                    justifyContent: "space-between",
                    gap: 16,
                }}
            >
                <div>
                    <h1 style={{ margin: 0 }}>COVID–GDP Dashboard</h1>
                    <p style={{ margin: "4px 0", color: "#555" }}>
                        Exploring Lotka–Volterra–style co-dynamics between{" "}
                        <strong>COVID-19</strong> and{" "}
                        <strong>economic activity</strong>.
                    </p>
                    <p style={{ margin: 0, fontSize: 12, color: "#777" }}>
                        COVID source: {covidSourceLabel}
                    </p>
                </div>

                {/* COVID Source Toggle */}
                <div
                    style={{
                        display: "flex",
                        alignItems: "center",
                        gap: 8,
                        background: "#f5f5f5",
                        padding: "4px 8px",
                        borderRadius: 999,
                    }}
                >
                    <span style={{ fontSize: 12, color: "#555" }}>
                        COVID data source:
                    </span>
                    <button
                        type="button"
                        onClick={() => setCovidSource("who")}
                        style={{
                            borderRadius: 999,
                            border: "none",
                            padding: "6px 10px",
                            fontSize: 12,
                            cursor: "pointer",
                            background:
                                covidSource === "who"
                                    ? "#1976d2"
                                    : "transparent",
                            color: covidSource === "who" ? "white" : "#1976d2",
                        }}
                    >
                        WHO
                    </button>
                    <button
                        type="button"
                        onClick={() => setCovidSource("te")}
                        style={{
                            borderRadius: 999,
                            border: "none",
                            padding: "6px 10px",
                            fontSize: 12,
                            cursor: "pointer",
                            background:
                                covidSource === "te"
                                    ? "#1976d2"
                                    : "transparent",
                            color: covidSource === "te" ? "white" : "#1976d2",
                        }}
                    >
                        TE
                    </button>
                </div>
            </header>

            {/* Country cards */}
            <section style={{ marginBottom: 24 }}>
                <h2 style={{ marginBottom: 8, fontSize: 18 }}>
                    Select country
                </h2>
                <div
                    style={{
                        display: "grid",
                        gridTemplateColumns:
                            "repeat(auto-fit, minmax(120px, 1fr))",
                        gap: 12,
                    }}
                >
                    {COUNTRY_CONFIG.map((c) => {
                        const active = c.key === selectedCountry;
                        return (
                            <button
                                key={c.key}
                                type="button"
                                onClick={() => setSelectedCountry(c.key)}
                                style={{
                                    padding: "10px 12px",
                                    textAlign: "left",
                                    borderRadius: 12,
                                    border: active
                                        ? "2px solid #1976d2"
                                        : "1px solid #ddd",
                                    background: active ? "#e3f2fd" : "white",
                                    cursor: "pointer",
                                    fontSize: 14,
                                }}
                            >
                                <div style={{ fontWeight: 600 }}>{c.label}</div>
                                <div style={{ fontSize: 11, color: "#777" }}>
                                    {c.key}
                                </div>
                            </button>
                        );
                    })}
                </div>
            </section>

            {/* Loading / Error */}
            {loading && <div style={{ marginTop: 24 }}>Loading data…</div>}
            {error && !loading && (
                <div style={{ marginTop: 24, color: "crimson" }}>
                    Error loading data: {error.message}
                </div>
            )}

            {!loading && !error && (
                <>
                    {/* Summary cards */}
                    <section
                        style={{
                            display: "grid",
                            gridTemplateColumns:
                                "repeat(auto-fit, minmax(180px, 1fr))",
                            gap: 16,
                            marginBottom: 24,
                        }}
                    >
                        <div
                            style={{
                                background: "white",
                                borderRadius: 12,
                                padding: 16,
                                border: "1px solid #eee",
                            }}
                        >
                            <div
                                style={{
                                    fontSize: 12,
                                    color: "#777",
                                    marginBottom: 4,
                                }}
                            >
                                Latest new cases
                            </div>
                            <div style={{ fontSize: 22, fontWeight: 700 }}>
                                {latest ? formatNumber(latest.newCases) : "—"}
                            </div>
                            <div
                                style={{
                                    fontSize: 11,
                                    color: "#999",
                                    marginTop: 4,
                                }}
                            >
                                Source:{" "}
                                {covidSource === "who" ? "WHO / Pomber" : "TE"}
                            </div>
                        </div>

                        <div
                            style={{
                                background: "white",
                                borderRadius: 12,
                                padding: 16,
                                border: "1px solid #eee",
                            }}
                        >
                            <div
                                style={{
                                    fontSize: 12,
                                    color: "#777",
                                    marginBottom: 4,
                                }}
                            >
                                Total confirmed
                            </div>
                            <div style={{ fontSize: 22, fontWeight: 700 }}>
                                {latest ? formatNumber(latest.confirmed) : "—"}
                            </div>
                        </div>

                        <div
                            style={{
                                background: "white",
                                borderRadius: 12,
                                padding: 16,
                                border: "1px solid #eee",
                            }}
                        >
                            <div
                                style={{
                                    fontSize: 12,
                                    color: "#777",
                                    marginBottom: 4,
                                }}
                            >
                                Total deaths
                            </div>
                            <div style={{ fontSize: 22, fontWeight: 700 }}>
                                {latest && latest.deaths != null
                                    ? formatNumber(latest.deaths)
                                    : "—"}
                            </div>
                        </div>

                        <div
                            style={{
                                background: "white",
                                borderRadius: 12,
                                padding: 16,
                                border: "1px solid #eee",
                            }}
                        >
                            <div
                                style={{
                                    fontSize: 12,
                                    color: "#777",
                                    marginBottom: 4,
                                }}
                            >
                                Latest GDP growth
                            </div>
                            <div style={{ fontSize: 22, fontWeight: 700 }}>
                                {latestGdp && latestGdp.gdpGrowth != null
                                    ? `${latestGdp.gdpGrowth.toFixed(1)}%`
                                    : "—"}
                            </div>
                        </div>
                    </section>

                    {/* Time-series chart */}
                    <section
                        style={{
                            background: "white",
                            borderRadius: 12,
                            padding: 16,
                            border: "1px solid #eee",
                            marginBottom: 24,
                        }}
                    >
                        <h2 style={{ margin: "0 0 8px", fontSize: 18 }}>
                            COVID vs GDP over time
                        </h2>
                        <p
                            style={{
                                margin: "0 0 12px",
                                fontSize: 12,
                                color: "#777",
                            }}
                        >
                            Left axis: new COVID cases per day. Right axis: GDP
                            growth (%). Notice how peaks in GDP can precede
                            peaks in new cases, echoing Lotka–Volterra
                            predator–prey dynamics.
                        </p>
                        <div style={{ width: "100%", height: 360 }}>
                            <ResponsiveContainer width="100%" height="100%">
                                <LineChart data={combined}>
                                    <CartesianGrid strokeDasharray="3 3" />
                                    <XAxis
                                        dataKey="date"
                                        tick={{ fontSize: 10 }}
                                        minTickGap={20}
                                    />
                                    <YAxis
                                        yAxisId="left"
                                        tick={{ fontSize: 10 }}
                                        tickFormatter={formatNumber}
                                    />
                                    <YAxis
                                        yAxisId="right"
                                        orientation="right"
                                        tick={{ fontSize: 10 }}
                                        tickFormatter={(v) =>
                                            v == null ? "" : `${v.toFixed(1)}%`
                                        }
                                    />
                                    <Tooltip
                                        formatter={(value, name) => {
                                            if (name === "New cases") {
                                                return [
                                                    formatNumber(value),
                                                    "New cases",
                                                ];
                                            }
                                            if (name === "GDP growth") {
                                                return [
                                                    value == null
                                                        ? "—"
                                                        : `${value.toFixed(
                                                              2
                                                          )}%`,
                                                    "GDP growth",
                                                ];
                                            }
                                            if (name === "New deaths") {
                                                return [
                                                    formatNumber(value),
                                                    "New deaths",
                                                ];
                                            }
                                            return [value, name];
                                        }}
                                    />
                                    <Legend />
                                    <Line
                                        yAxisId="left"
                                        type="monotone"
                                        dataKey="newCases"
                                        name="New cases"
                                        dot={false}
                                        stroke="#1976d2"
                                        strokeWidth={1.5}
                                    />
                                    <Line
                                        yAxisId="left"
                                        type="monotone"
                                        dataKey="newDeaths"
                                        name="New deaths"
                                        dot={false}
                                        stroke="#d32f2f"
                                        strokeWidth={1}
                                    />
                                    <Line
                                        yAxisId="right"
                                        type="monotone"
                                        dataKey="gdpGrowth"
                                        name="GDP growth"
                                        dot={false}
                                        stroke="#388e3c"
                                        strokeWidth={1.5}
                                    />
                                </LineChart>
                            </ResponsiveContainer>
                        </div>
                    </section>
                </>
            )}
        </div>
    );
}

export default Dashboard;
