// src/pages/MapView.js
import React, { useState, useMemo } from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";
import SummaryCard from "../components/SummaryCard";
import { useCovidGdpApiData } from "../hooks/useCovidGdpApiData";
import { formatNumber } from "../utils/formatters";
import { computeTrend } from "../utils/dataTransforms";

// World countries TopoJSON
const GEO_URL =
    "https://raw.githubusercontent.com/deldersveld/topojson/master/world-countries.json";

// Same country list you use in Dashboard
const COUNTRY_CONFIG = [
    { key: "US", label: "United States" },
    { key: "DE", label: "Germany" },
    { key: "IT", label: "Italy" },
    { key: "JP", label: "Japan" },
    { key: "CA", label: "Canada" },
    { key: "FR", label: "France" },
    { key: "SE", label: "Sweden" },
    { key: "MX", label: "Mexico" },
    { key: "NZ", label: "New Zealand" },
    { key: "TH", label: "Thailand" },
];

// Map our 2-letter codes to ISO_A3 codes used in the geo dataset
const CODE_TO_A3 = {
    US: "USA",
    DE: "DEU",
    IT: "ITA",
    JP: "JPN",
    CA: "CAN",
    FR: "FRA",
    SE: "SWE",
    MX: "MEX",
    NZ: "NZL",
    TH: "THA",
};

const A3_TO_CODE = Object.fromEntries(
    Object.entries(CODE_TO_A3).map(([k, v]) => [v, k])
);

const lightTheme = {
    background: "#f5f5f5",
    textPrimary: "#111",
    textSecondary: "#555",
    textMuted: "#777",
    cardBg: "#ffffff",
    border: "#e0e0e0",
};

function MapView() {
    const [selectedCountry, setSelectedCountry] = useState("US");
    const [covidSource, setCovidSource] = useState("who"); // "who" | "te"

    const theme = lightTheme;

    const { loading, error, combined, covidDaily } = useCovidGdpApiData(
        selectedCountry,
        covidSource
    );

    const latestCovid = useMemo(() => {
        if (!covidDaily || covidDaily.length === 0) return null;
        return covidDaily[covidDaily.length - 1];
    }, [covidDaily]);

    const latestGdp = useMemo(() => {
        if (!combined || combined.length === 0) return null;
        const lastWithGdp = [...combined]
            .reverse()
            .find((d) => d.gdpGrowth != null);
        return lastWithGdp || null;
    }, [combined]);

    const newCasesSeries = combined ? combined.map((d) => d.newCases) : [];
    const gdpSeries = combined ? combined.map((d) => d.gdpGrowth) : [];

    const casesTrend = useMemo(
        () => computeTrend(newCasesSeries, 7),
        [newCasesSeries]
    );
    const gdpTrend = useMemo(() => computeTrend(gdpSeries, 8), [gdpSeries]);

    const covidSourceLabel =
        covidSource === "who"
            ? "WHO / Pomber timeseries"
            : "TradingEconomics (fallback to WHO)";

    const selectedConfig = COUNTRY_CONFIG.find(
        (c) => c.key === selectedCountry
    );

    return (
        <div className="dashboard-container">
            {/* Header */}
            <header className="dashboard-header">
                <div>
                    <h1 style={{ margin: 0 }}>Global Map View</h1>
                    <p
                        style={{
                            margin: "4px 0",
                            color: theme.textSecondary,
                            maxWidth: 540,
                        }}
                    >
                        Click a highlighted country on the map to see its latest
                        <strong> COVID-19 </strong>
                        and
                        <strong> GDP growth </strong>
                        metrics. This reuses the same data as the main
                        dashboard.
                    </p>
                    <p
                        style={{
                            margin: 0,
                            fontSize: 12,
                            color: theme.textMuted,
                        }}
                    >
                        COVID source: {covidSourceLabel}
                    </p>
                </div>

                {/* COVID Source Toggle */}
                <div className="pill-toggle">
                    <span className="pill-toggle-label">COVID data:</span>
                    <button
                        type="button"
                        onClick={() => setCovidSource("who")}
                        className={covidSource === "who" ? "is-active" : ""}
                    >
                        WHO
                    </button>
                    <button
                        type="button"
                        onClick={() => setCovidSource("te")}
                        className={covidSource === "te" ? "is-active" : ""}
                    >
                        TE
                    </button>
                </div>
            </header>

            {/* Map section */}
            <section className="section">
                <div className="map-card">
                    <h2 className="section-title">World map</h2>
                    <p className="section-subtitle">
                        Highlighted countries are those with COVID + GDP data in
                        this dashboard. Click one to select it.
                    </p>
                    <div className="map-wrapper">
                        <ComposableMap
                            projectionConfig={{ scale: 145 }}
                            style={{ width: "100%", height: "100%" }}
                        >
                            <Geographies geography={GEO_URL}>
                                {({ geographies }) =>
                                    geographies.map((geo) => {
                                        const isoA3 = geo.properties.ISO_A3;
                                        const code = A3_TO_CODE[isoA3]; // one of US, DE, ...
                                        const isSupported = !!code;
                                        const isSelected =
                                            code === selectedCountry;

                                        const defaultFill = isSupported
                                            ? "#e0f2f1"
                                            : "#f0f0f0";

                                        return (
                                            <Geography
                                                key={geo.rsmKey}
                                                geography={geo}
                                                onClick={() => {
                                                    if (code) {
                                                        setSelectedCountry(
                                                            code
                                                        );
                                                    }
                                                }}
                                                style={{
                                                    default: {
                                                        fill: isSelected
                                                            ? "#1976d2"
                                                            : defaultFill,
                                                        stroke: "#ffffff",
                                                        strokeWidth: 0.5,
                                                        outline: "none",
                                                    },
                                                    hover: {
                                                        fill: isSupported
                                                            ? "#80cbc4"
                                                            : "#e0e0e0",
                                                        stroke: "#ffffff",
                                                        strokeWidth: 0.7,
                                                        outline: "none",
                                                    },
                                                    pressed: {
                                                        fill: isSupported
                                                            ? "#1976d2"
                                                            : defaultFill,
                                                        stroke: "#ffffff",
                                                        strokeWidth: 0.7,
                                                        outline: "none",
                                                    },
                                                }}
                                            />
                                        );
                                    })
                                }
                            </Geographies>
                        </ComposableMap>
                    </div>
                </div>
            </section>

            {/* Loading / error for data */}
            {loading && <div className="loading-text">Loading data…</div>}
            {error && !loading && (
                <div className="error-text">
                    Error loading data: {error.message}
                </div>
            )}

            {/* Summary metrics for selected country */}
            {!loading && !error && (
                <section className="section summary-grid">
                    <SummaryCard
                        theme={theme}
                        title="Country"
                        value={
                            selectedConfig
                                ? `${selectedConfig.label} (${selectedConfig.key})`
                                : selectedCountry
                        }
                        subtitle="Selected on the map"
                    />
                    <SummaryCard
                        theme={theme}
                        title="Latest new cases"
                        value={
                            latestCovid
                                ? formatNumber(latestCovid.newCases)
                                : "—"
                        }
                        subtitle={`Source: ${
                            covidSource === "who" ? "WHO / Pomber" : "TE"
                        }`}
                        trend={casesTrend}
                        trendLabel="7-day trend"
                    />
                    <SummaryCard
                        theme={theme}
                        title="Total confirmed"
                        value={
                            latestCovid
                                ? formatNumber(latestCovid.confirmed)
                                : "—"
                        }
                    />
                    <SummaryCard
                        theme={theme}
                        title="Total deaths"
                        value={
                            latestCovid && latestCovid.deaths != null
                                ? formatNumber(latestCovid.deaths)
                                : "—"
                        }
                    />
                    <SummaryCard
                        theme={theme}
                        title="Latest GDP growth"
                        value={
                            latestGdp && latestGdp.gdpGrowth != null
                                ? `${latestGdp.gdpGrowth.toFixed(1)}%`
                                : "—"
                        }
                        subtitle="GDP: World Bank + TradingEconomics (token)"
                        trend={gdpTrend}
                        trendLabel="Recent trend"
                    />
                </section>
            )}
        </div>
    );
}

export default MapView;
