// src/pages/Dashboard.js
import React, { useState, useMemo } from "react";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Tooltip,
    Legend,
    Title,
} from "chart.js";
import { useCovidGdpApiData } from "../hooks/useCovidGdpApiData";
import SummaryCard from "../components/SummaryCard";
import DataPreviewTable from "../components/DataPreviewTable";
import { formatNumber } from "../utils/formatters";
import { rollingAverage, computeTrend } from "../utils/dataTransforms";

// Register Chart.js components
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Tooltip,
    Legend,
    Title
);

const COUNTRY_CONFIG = [
    { key: "US", label: "United States" },
    { key: "DE", label: "Germany" },
    { key: "IT", label: "Italy" },
    { key: "JP", label: "Japan" },
    { key: "CA", label: "Canada" },
    { key: "FR", label: "France" },
    { key: "SE", label: "Sweden" },
    { key: "MX", label: "Mexico" },
    { key: "NZ", label: "New Zealand" },
    { key: "TH", label: "Thailand" },
];

const lightTheme = {
    background: "#f5f5f5",
    textPrimary: "#111",
    textSecondary: "#555",
    textMuted: "#777",
    cardBg: "#ffffff",
    border: "#e0e0e0",
    pillBg: "#f5f5f5",
    tableHeaderBg: "#fafafa",
    tableRowBorder: "#f3f3f3",
};

function Dashboard() {
    const [selectedCountry, setSelectedCountry] = useState("US");
    const [compareCountry, setCompareCountry] = useState("DE");
    const [covidSource, setCovidSource] = useState("who"); // "who" | "te"
    const [viewMode, setViewMode] = useState("single"); // "single" | "compare"
    const [showRolling, setShowRolling] = useState(false);

    const theme = lightTheme;

    // Primary country data
    const {
        loading: loadingA,
        error: errorA,
        combined: combinedA,
        covidDaily: covidDailyA,
    } = useCovidGdpApiData(selectedCountry, covidSource);

    const latestA = useMemo(() => {
        if (!covidDailyA || covidDailyA.length === 0) return null;
        return covidDailyA[covidDailyA.length - 1];
    }, [covidDailyA]);

    const latestGdpA = useMemo(() => {
        if (!combinedA || combinedA.length === 0) return null;
        const lastWithGdp = [...combinedA]
            .reverse()
            .find((d) => d.gdpGrowth != null);
        return lastWithGdp || null;
    }, [combinedA]);

    const covidSourceLabel =
        covidSource === "who"
            ? "WHO / Pomber timeseries"
            : "TradingEconomics (fallback to WHO)";

    // ----- Single view datasets -----
    const labelsA = combinedA ? combinedA.map((d) => d.date) : [];

    const newCasesA = combinedA ? combinedA.map((d) => d.newCases) : [];
    const newDeathsA = combinedA ? combinedA.map((d) => d.newDeaths || 0) : [];
    const gdpA = combinedA ? combinedA.map((d) => d.gdpGrowth) : [];

    const newCasesPlotA = showRolling
        ? rollingAverage(newCasesA, 7)
        : newCasesA;

    // Trend signals (like stock up/down)
    const casesTrend = useMemo(() => computeTrend(newCasesA, 7), [newCasesA]);
    const gdpTrend = useMemo(() => computeTrend(gdpA, 8), [gdpA]);

    const timeSeriesDataSingle = {
        labels: labelsA,
        datasets: [
            {
                label: showRolling ? "New cases (7-day avg)" : "New cases",
                data: newCasesPlotA,
                borderColor: "#1976d2",
                backgroundColor: "rgba(25, 118, 210, 0.15)",
                borderWidth: 1.5,
                pointRadius: 0,
                tension: 0.25,
                yAxisID: "yCases",
            },
            {
                label: "GDP growth (%)",
                data: gdpA,
                borderColor: "#388e3c",
                backgroundColor: "rgba(56, 142, 60, 0.15)",
                borderWidth: 1.5,
                pointRadius: 0,
                tension: 0.25,
                yAxisID: "yGdp",
            },
        ],
    };

    const timeSeriesOptionsSingle = {
        responsive: true,
        maintainAspectRatio: false,
        interaction: { mode: "index", intersect: false },
        plugins: {
            legend: {
                position: "top",
                labels: { font: { size: 11 }, color: theme.textPrimary },
            },
            title: {
                display: true,
                text: "COVID-19 & GDP over time",
                font: { size: 14 },
                color: theme.textPrimary,
            },
            tooltip: {
                callbacks: {
                    label: function (ctx) {
                        const label = ctx.dataset.label || "";
                        const value = ctx.raw;
                        if (label.toLowerCase().includes("cases")) {
                            return `${label}: ${formatNumber(value)}`;
                        }
                        if (label.includes("GDP growth")) {
                            if (value == null) return `${label}: —`;
                            return `${label}: ${value.toFixed(2)}%`;
                        }
                        return `${label}: ${value}`;
                    },
                },
            },
        },
        scales: {
            x: {
                ticks: {
                    maxTicksLimit: 10,
                    font: { size: 10 },
                    color: theme.textSecondary,
                },
                grid: { color: theme.border },
            },
            yCases: {
                type: "linear",
                position: "left",
                title: {
                    display: true,
                    text: "New cases",
                    color: theme.textSecondary,
                },
                ticks: {
                    callback: (v) => formatNumber(v),
                    font: { size: 10 },
                    color: theme.textSecondary,
                },
                grid: { color: theme.border },
            },
            yGdp: {
                type: "linear",
                position: "right",
                title: {
                    display: true,
                    text: "GDP growth (%)",
                    color: theme.textSecondary,
                },
                grid: { drawOnChartArea: false, color: theme.border },
                ticks: {
                    callback: (v) => (v == null ? "" : `${v.toFixed(1)}%`),
                    font: { size: 10 },
                    color: theme.textSecondary,
                },
            },
        },
    };

    // ----- Comparison view -----
    const {
        loading: loadingB,
        error: errorB,
        combined: combinedB,
        covidDaily: covidDailyB,
    } = useCovidGdpApiData(compareCountry, covidSource);

    const latestB = useMemo(() => {
        if (!covidDailyB || covidDailyB.length === 0) return null;
        return covidDailyB[covidDailyB.length - 1];
    }, [covidDailyB]);

    const latestGdpB = useMemo(() => {
        if (!combinedB || combinedB.length === 0) return null;
        const lastWithGdp = [...combinedB]
            .reverse()
            .find((d) => d.gdpGrowth != null);
        return lastWithGdp || null;
    }, [combinedB]);

    const labelsCompare = combinedA ? combinedA.map((d) => d.date) : [];
    const mapB = new Map((combinedB || []).map((d) => [d.date, d]));

    const casesACompare = combinedA ? combinedA.map((d) => d.newCases) : [];
    const casesBCompare = labelsCompare.map((date) => {
        const row = mapB.get(date);
        return row ? row.newCases : null;
    });

    const gdpACompare = combinedA ? combinedA.map((d) => d.gdpGrowth) : [];
    const gdpBCompare = labelsCompare.map((date) => {
        const row = mapB.get(date);
        return row ? row.gdpGrowth : null;
    });

    const timeSeriesDataCompare = {
        labels: labelsCompare,
        datasets: [
            {
                label: `New cases (${selectedCountry})`,
                data: casesACompare,
                borderColor: "#1976d2",
                backgroundColor: "rgba(25, 118, 210, 0.15)",
                borderWidth: 1.5,
                pointRadius: 0,
                tension: 0.25,
                yAxisID: "yCases",
            },
            {
                label: `New cases (${compareCountry})`,
                data: casesBCompare,
                borderColor: "#ff9800",
                backgroundColor: "rgba(255, 152, 0, 0.12)",
                borderWidth: 1.5,
                pointRadius: 0,
                tension: 0.25,
                yAxisID: "yCases",
            },
            {
                label: `GDP growth (%) (${selectedCountry})`,
                data: gdpACompare,
                borderColor: "#388e3c",
                backgroundColor: "rgba(56, 142, 60, 0.15)",
                borderWidth: 1.5,
                pointRadius: 0,
                tension: 0.25,
                yAxisID: "yGdp",
            },
            {
                label: `GDP growth (%) (${compareCountry})`,
                data: gdpBCompare,
                borderColor: "#9c27b0",
                backgroundColor: "rgba(156, 39, 176, 0.15)",
                borderWidth: 1.5,
                pointRadius: 0,
                tension: 0.25,
                yAxisID: "yGdp",
            },
        ],
    };

    const timeSeriesOptionsCompare = {
        responsive: true,
        maintainAspectRatio: false,
        interaction: { mode: "index", intersect: false },
        plugins: {
            legend: {
                position: "top",
                labels: { font: { size: 11 }, color: theme.textPrimary },
            },
            title: {
                display: true,
                text: "Country comparison: COVID-19 & GDP",
                font: { size: 14 },
                color: theme.textPrimary,
            },
            tooltip: {
                callbacks: {
                    label: function (ctx) {
                        const label = ctx.dataset.label || "";
                        const value = ctx.raw;
                        if (label.toLowerCase().includes("cases")) {
                            return `${label}: ${formatNumber(value)}`;
                        }
                        if (label.includes("GDP growth")) {
                            if (value == null) return `${label}: —`;
                            return `${label}: ${value.toFixed(2)}%`;
                        }
                        return `${label}: ${value}`;
                    },
                },
            },
        },
        scales: {
            x: {
                ticks: {
                    maxTicksLimit: 10,
                    font: { size: 10 },
                    color: theme.textSecondary,
                },
                grid: { color: theme.border },
            },
            yCases: {
                type: "linear",
                position: "left",
                title: {
                    display: true,
                    text: "New cases",
                    color: theme.textSecondary,
                },
                ticks: {
                    callback: (v) => formatNumber(v),
                    font: { size: 10 },
                    color: theme.textSecondary,
                },
                grid: { color: theme.border },
            },
            yGdp: {
                type: "linear",
                position: "right",
                title: {
                    display: true,
                    text: "GDP growth (%)",
                    color: theme.textSecondary,
                },
                grid: { drawOnChartArea: false, color: theme.border },
                ticks: {
                    callback: (v) => (v == null ? "" : `${v.toFixed(1)}%`),
                    font: { size: 10 },
                    color: theme.textSecondary,
                },
            },
        },
    };

    const loading = viewMode === "single" ? loadingA : loadingA || loadingB;
    const error = viewMode === "single" ? errorA : errorA || errorB;

    const containerStyle = {
        padding: "24px",
        maxWidth: 1200,
        margin: "0 auto",
        backgroundColor: theme.background,
        minHeight: "100vh",
        color: theme.textPrimary,
    };

    return (
        <div style={containerStyle}>
            {/* Header */}
            <header
                style={{
                    marginBottom: 24,
                    display: "flex",
                    flexWrap: "wrap",
                    alignItems: "center",
                    justifyContent: "space-between",
                    gap: 16,
                }}
            >
                <div>
                    <h1 style={{ margin: 0 }}>COVID–GDP Dashboard</h1>
                    <p
                        style={{
                            margin: "4px 0",
                            color: theme.textSecondary,
                            maxWidth: 520,
                        }}
                    >
                        Visualizing how{" "}
                        <strong>COVID-19 cases and deaths</strong> relate to{" "}
                        <strong>economic activity (GDP growth)</strong> across
                        countries, using public datasets and a token-protected
                        GDP API.
                    </p>
                    <p
                        style={{
                            margin: 0,
                            fontSize: 12,
                            color: theme.textMuted,
                        }}
                    >
                        COVID source: {covidSourceLabel}
                    </p>
                </div>

                {/* COVID Source Toggle */}
                <div
                    style={{
                        display: "flex",
                        alignItems: "center",
                        gap: 8,
                        background: theme.pillBg,
                        padding: "4px 8px",
                        borderRadius: 999,
                        border: `1px solid ${theme.border}`,
                    }}
                >
                    <span style={{ fontSize: 12, color: theme.textSecondary }}>
                        COVID data:
                    </span>
                    <button
                        type="button"
                        onClick={() => setCovidSource("who")}
                        style={{
                            borderRadius: 999,
                            border: "none",
                            padding: "6px 10px",
                            fontSize: 12,
                            cursor: "pointer",
                            background:
                                covidSource === "who"
                                    ? "#1976d2"
                                    : "transparent",
                            color: covidSource === "who" ? "white" : "#1976d2",
                        }}
                    >
                        WHO
                    </button>
                    <button
                        type="button"
                        onClick={() => setCovidSource("te")}
                        style={{
                            borderRadius: 999,
                            border: "none",
                            padding: "6px 10px",
                            fontSize: 12,
                            cursor: "pointer",
                            background:
                                covidSource === "te"
                                    ? "#1976d2"
                                    : "transparent",
                            color: covidSource === "te" ? "white" : "#1976d2",
                        }}
                    >
                        TE
                    </button>
                </div>
            </header>

            {/* View mode toggle */}
            <section style={{ marginBottom: 16 }}>
                <div
                    style={{
                        display: "inline-flex",
                        alignItems: "center",
                        borderRadius: 999,
                        border: `1px solid ${theme.border}`,
                        overflow: "hidden",
                        background: theme.pillBg,
                    }}
                >
                    <button
                        type="button"
                        onClick={() => setViewMode("single")}
                        style={{
                            padding: "6px 16px",
                            border: "none",
                            cursor: "pointer",
                            fontSize: 12,
                            background:
                                viewMode === "single"
                                    ? "#1976d2"
                                    : "transparent",
                            color:
                                viewMode === "single"
                                    ? "white"
                                    : theme.textSecondary,
                        }}
                    >
                        Single country
                    </button>
                    <button
                        type="button"
                        onClick={() => setViewMode("compare")}
                        style={{
                            padding: "6px 16px",
                            border: "none",
                            cursor: "pointer",
                            fontSize: 12,
                            background:
                                viewMode === "compare"
                                    ? "#1976d2"
                                    : "transparent",
                            color:
                                viewMode === "compare"
                                    ? "white"
                                    : theme.textSecondary,
                        }}
                    >
                        Compare countries
                    </button>
                </div>
            </section>

            {/* Country selection */}
            <section style={{ marginBottom: 24 }}>
                <h2 style={{ marginBottom: 8, fontSize: 18 }}>
                    Select country
                </h2>
                <div
                    style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(5, 1fr)", // 5 cards per row
                        gap: 12,
                    }}
                >
                    {COUNTRY_CONFIG.map((c) => {
                        const active = c.key === selectedCountry;
                        return (
                            <button
                                key={c.key}
                                type="button"
                                onClick={() => setSelectedCountry(c.key)}
                                style={{
                                    padding: "10px 12px",
                                    textAlign: "left",
                                    borderRadius: 12,
                                    border: active
                                        ? "2px solid #1976d2"
                                        : `1px solid ${theme.border}`,
                                    background: active
                                        ? "#e3f2fd"
                                        : theme.cardBg,
                                    cursor: "pointer",
                                    fontSize: 14,
                                    color: theme.textPrimary,
                                }}
                            >
                                <div style={{ fontWeight: 600 }}>{c.label}</div>
                                <div
                                    style={{
                                        fontSize: 11,
                                        color: theme.textMuted,
                                    }}
                                >
                                    {c.key}
                                </div>
                            </button>
                        );
                    })}
                </div>
            </section>

            {/* Compare country selection */}
            {viewMode === "compare" && (
                <section style={{ marginBottom: 24 }}>
                    <h3 style={{ marginBottom: 8, fontSize: 16 }}>
                        Compare with another country
                    </h3>
                    <div
                        style={{
                            display: "flex",
                            flexWrap: "wrap",
                            gap: 8,
                        }}
                    >
                        {COUNTRY_CONFIG.filter(
                            (c) => c.key !== selectedCountry
                        ).map((c) => {
                            const active = c.key === compareCountry;
                            return (
                                <button
                                    key={c.key}
                                    type="button"
                                    onClick={() => setCompareCountry(c.key)}
                                    style={{
                                        padding: "6px 10px",
                                        borderRadius: 999,
                                        border: active
                                            ? "2px solid #1976d2"
                                            : `1px solid ${theme.border}`,
                                        background: active
                                            ? "#e3f2fd"
                                            : theme.cardBg,
                                        cursor: "pointer",
                                        fontSize: 12,
                                        color: theme.textPrimary,
                                    }}
                                >
                                    {c.label}
                                </button>
                            );
                        })}
                    </div>
                </section>
            )}

            {/* Loading / Error */}
            {loading && <div style={{ marginTop: 24 }}>Loading data…</div>}
            {error && !loading && (
                <div style={{ marginTop: 24, color: "crimson" }}>
                    Error loading data: {error.message}
                </div>
            )}

            {/* SINGLE VIEW */}
            {!loading && !error && viewMode === "single" && (
                <>
                    {/* Summary cards */}
                    <section
                        style={{
                            display: "grid",
                            gridTemplateColumns:
                                "repeat(auto-fit, minmax(180px, 1fr))",
                            gap: 16,
                            marginBottom: 24,
                        }}
                    >
                        <SummaryCard
                            theme={theme}
                            title="Latest new cases"
                            value={
                                latestA ? formatNumber(latestA.newCases) : "—"
                            }
                            subtitle={`Source: ${
                                covidSource === "who" ? "WHO / Pomber" : "TE"
                            }`}
                            trend={casesTrend}
                            trendLabel="7-day trend"
                        />
                        <SummaryCard
                            theme={theme}
                            title="Total confirmed"
                            value={
                                latestA ? formatNumber(latestA.confirmed) : "—"
                            }
                        />
                        <SummaryCard
                            theme={theme}
                            title="Total deaths"
                            value={
                                latestA && latestA.deaths != null
                                    ? formatNumber(latestA.deaths)
                                    : "—"
                            }
                        />
                        <SummaryCard
                            theme={theme}
                            title="Latest GDP growth"
                            value={
                                latestGdpA && latestGdpA.gdpGrowth != null
                                    ? `${latestGdpA.gdpGrowth.toFixed(1)}%`
                                    : "—"
                            }
                            subtitle="GDP: World Bank + TradingEconomics (token)"
                            trend={gdpTrend}
                            trendLabel="Recent trend"
                        />
                    </section>

                    {/* Rolling toggle */}
                    <section
                        style={{
                            marginBottom: 8,
                            display: "flex",
                            justifyContent: "flex-end",
                        }}
                    >
                        <button
                            type="button"
                            onClick={() => setShowRolling((v) => !v)}
                            style={{
                                borderRadius: 999,
                                border: `1px solid ${theme.border}`,
                                padding: "4px 10px",
                                fontSize: 12,
                                cursor: "pointer",
                                background: theme.cardBg,
                                color: theme.textPrimary,
                            }}
                        >
                            {showRolling
                                ? "Show daily values"
                                : "Show 7-day averages"}
                        </button>
                    </section>

                    {/* Time-series chart */}
                    <section
                        style={{
                            background: theme.cardBg,
                            borderRadius: 12,
                            padding: 16,
                            border: `1px solid ${theme.border}`,
                            marginBottom: 24,
                        }}
                    >
                        <h2 style={{ margin: "0 0 8px", fontSize: 18 }}>
                            COVID vs GDP over time
                        </h2>
                        <p
                            style={{
                                margin: "0 0 8px",
                                fontSize: 12,
                                color: theme.textMuted,
                            }}
                        >
                            Left axis: new COVID cases per day. Right axis: GDP
                            growth (%). The summary cards above show short-term
                            trends, similar to how stock dashboards indicate
                            whether a metric is rising, falling, or flat.
                        </p>
                        <div style={{ width: "100%", height: 380 }}>
                            <Line
                                data={timeSeriesDataSingle}
                                options={timeSeriesOptionsSingle}
                            />
                        </div>
                    </section>

                    {/* Data preview */}
                    <DataPreviewTable
                        theme={theme}
                        combined={combinedA}
                        title="Sample of joined data"
                    />
                </>
            )}

            {/* COMPARE VIEW */}
            {!loading && !error && viewMode === "compare" && (
                <>
                    {/* Summary cards */}
                    <section
                        style={{
                            display: "grid",
                            gridTemplateColumns:
                                "repeat(auto-fit, minmax(220px, 1fr))",
                            gap: 16,
                            marginBottom: 24,
                        }}
                    >
                        <SummaryCard
                            theme={theme}
                            title={`Latest new cases (${selectedCountry})`}
                            value={
                                latestA ? formatNumber(latestA.newCases) : "—"
                            }
                            subtitle={`Confirmed: ${
                                latestA ? formatNumber(latestA.confirmed) : "—"
                            }`}
                        />
                        <SummaryCard
                            theme={theme}
                            title={`Latest new cases (${compareCountry})`}
                            value={
                                latestB ? formatNumber(latestB.newCases) : "—"
                            }
                            subtitle={`Confirmed: ${
                                latestB ? formatNumber(latestB.confirmed) : "—"
                            }`}
                        />
                        <SummaryCard
                            theme={theme}
                            title={`Latest GDP growth (${selectedCountry})`}
                            value={
                                latestGdpA && latestGdpA.gdpGrowth != null
                                    ? `${latestGdpA.gdpGrowth.toFixed(1)}%`
                                    : "—"
                            }
                        />
                        <SummaryCard
                            theme={theme}
                            title={`Latest GDP growth (${compareCountry})`}
                            value={
                                latestGdpB && latestGdpB.gdpGrowth != null
                                    ? `${latestGdpB.gdpGrowth.toFixed(1)}%`
                                    : "—"
                            }
                        />
                    </section>

                    {/* Comparison chart */}
                    <section
                        style={{
                            background: theme.cardBg,
                            borderRadius: 12,
                            padding: 16,
                            border: `1px solid ${theme.border}`,
                            marginBottom: 24,
                        }}
                    >
                        <h2 style={{ margin: "0 0 8px", fontSize: 18 }}>
                            Country comparison: COVID & GDP
                        </h2>
                        <p
                            style={{
                                margin: "0 0 12px",
                                fontSize: 12,
                                color: theme.textMuted,
                            }}
                        >
                            Compare daily new cases and GDP growth between two
                            countries. Differences in policy, timing, and
                            vaccine rollout often show up as different shapes in
                            these curves.
                        </p>
                        <div style={{ width: "100%", height: 380 }}>
                            <Line
                                data={timeSeriesDataCompare}
                                options={timeSeriesOptionsCompare}
                            />
                        </div>
                    </section>

                    {/* Data preview */}
                    <DataPreviewTable
                        theme={theme}
                        combined={combinedA}
                        title={`Sample data (${selectedCountry})`}
                    />
                </>
            )}
        </div>
    );
}

export default Dashboard;
