import { useEffect, useState } from "react";

// Map dashboard country code → pomber COVID name
const COVID_COUNTRY_MAP = {
    US: "US",
    DE: "Germany",
    IT: "Italy",
    JP: "Japan",
    CA: "Canada",
    FR: "France",
};

/**
 * Fetch COVID-19 daily time series (pombér)
 * + FRED real GDP (via our local proxy),
 * then join them on date by using the most recent GDP value
 * at or before each COVID date and computing quarter-over-quarter growth.
 */
export function useCovidGdpApiData(countryKey) {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [covidDaily, setCovidDaily] = useState([]);
    const [gdpSeries, setGdpSeries] = useState([]);
    const [combined, setCombined] = useState([]);

    const covidKey = COVID_COUNTRY_MAP[countryKey] || countryKey;
    const proxyBase =
        process.env.REACT_APP_FRED_PROXY_URL || "http://localhost:5001";

    useEffect(() => {
        async function fetchData() {
            setLoading(true);
            setError(null);

            try {
                //
                // 1. COVID daily time series
                //
                const covidResp = await fetch(
                    "https://pomber.github.io/covid19/timeseries.json"
                );
                if (!covidResp.ok) {
                    throw new Error("COVID API request failed");
                }
                const covidAll = await covidResp.json();
                const series = covidAll[covidKey];
                if (!series) {
                    throw new Error(`No COVID data for country: ${covidKey}`);
                }

                const covidWithNew = series.map((row, idx) => {
                    const prevConfirmed =
                        idx > 0 ? series[idx - 1].confirmed : 0;
                    const newCases = row.confirmed - prevConfirmed;
                    return {
                        date: row.date,
                        confirmed: row.confirmed,
                        deaths: row.deaths,
                        recovered: row.recovered,
                        newCases: newCases < 0 ? 0 : newCases,
                    };
                });

                //
                // 2. FRED GDP via proxy
                //
                const gdpResp = await fetch(
                    `${proxyBase}/fred/gdp?country=${encodeURIComponent(
                        countryKey
                    )}`
                );
                if (!gdpResp.ok) {
                    const txt = await gdpResp.text();
                    throw new Error(
                        `FRED proxy error (${gdpResp.status}): ${
                            txt || "unknown"
                        }`
                    );
                }
                const gdpJson = await gdpResp.json();
                const observations = gdpJson.observations || [];

                const gdpWithGrowth = observations.map((obs, idx) => {
                    const value = Number(obs.value);
                    if (idx === 0) {
                        return {
                            date: obs.date,
                            value,
                            growth: null,
                        };
                    }
                    const prev = Number(observations[idx - 1].value);
                    const growth =
                        prev === 0 || Number.isNaN(prev)
                            ? null
                            : ((value - prev) / prev) * 100;
                    return {
                        date: obs.date,
                        value,
                        growth,
                    };
                });

                //
                // 3. Join daily COVID with nearest GDP growth
                //
                let j = 0;
                const joined = covidWithNew.map((day) => {
                    const dayDate = day.date;
                    while (
                        j + 1 < gdpWithGrowth.length &&
                        gdpWithGrowth[j + 1].date <= dayDate
                    ) {
                        j += 1;
                    }
                    const gdpForDay = gdpWithGrowth[j] || null;
                    return {
                        date: day.date,
                        newCases: day.newCases,
                        confirmed: day.confirmed,
                        deaths: day.deaths,
                        gdpGrowth: gdpForDay ? gdpForDay.growth : null,
                    };
                });

                setCovidDaily(covidWithNew);
                setGdpSeries(gdpWithGrowth);
                setCombined(joined);
                setLoading(false);
            } catch (err) {
                console.error("useCovidGdpApiData error:", err);
                setError(err);
                setLoading(false);
            }
        }

        fetchData();
    }, [countryKey, covidKey, proxyBase]);

    return { loading, error, covidDaily, gdpSeries, combined };
}
