// src/components/SummaryCard.js
import React from "react";

function SummaryCard({ theme, title, value, subtitle, trend, trendLabel }) {
    let trendText = null;
    let trendColor = theme.textMuted;

    if (trend && trend.direction) {
        const dir = trend.direction;
        const pct = trend.pct;
        let arrow = "→";
        let word = "Flat";

        if (dir === "up") {
            arrow = "↑";
            word = "Rising";
            trendColor = "#2e7d32"; // green-ish
        } else if (dir === "down") {
            arrow = "↓";
            word = "Falling";
            trendColor = "#c62828"; // red-ish
        }

        const pctText = pct != null ? ` (${pct.toFixed(1)}% over window)` : "";

        trendText = `${trendLabel || "Trend"}: ${arrow} ${word}${pctText}`;
    }

    return (
        <div
            style={{
                background: theme.cardBg,
                borderRadius: 12,
                padding: 16,
                border: `1px solid ${theme.border}`,
            }}
        >
            <div
                style={{
                    fontSize: 12,
                    color: theme.textMuted,
                    marginBottom: 4,
                }}
            >
                {title}
            </div>
            <div style={{ fontSize: 22, fontWeight: 700 }}>{value}</div>
            {subtitle && (
                <div
                    style={{
                        fontSize: 11,
                        color: theme.textMuted,
                        marginTop: 4,
                    }}
                >
                    {subtitle}
                </div>
            )}
            {trendText && (
                <div
                    style={{
                        fontSize: 11,
                        color: trendColor,
                        marginTop: 4,
                    }}
                >
                    {trendText}
                </div>
            )}
        </div>
    );
}

export default SummaryCard;
